SELECT DISTINCT mg._id,mg.destroyed,mg.owner,mg.name,(CASE WHEN name LIKE "" THEN 'NAME' ELSE 'CONTENT' END) AS containsType FROM (SELECT * FROM sd_message_group WHERE _id IN ("1","0","535761e21801b518bc73708d5d07427f","6537db4923acc558d63cd8391ceb9e43","16fcf48fb042903b9d580aec7f4e5d0d")) AS mg LEFT JOIN (SELECT * FROM sd_messages WHERE `to` IN ("1","0","535761e21801b518bc73708d5d07427f","6537db4923acc558d63cd8391ceb9e43","16fcf48fb042903b9d580aec7f4e5d0d") ) AS m ON mg._id = m.`to` WHERE name LIKE "" OR m.messageJson LIKE "" ORDER BY (CASE WHEN _order IS NULL THEN 0 ELSE _order END) DESC, name